import React, {useState} from 'react';
import Typical from 'react-typical'
import Background from '../Components/img/chicago.jpg';
import emailjs from 'emailjs-com'
import {navigate} from '@reach/router';


const LandingPage=(props)=>{
    const [name, setName]=useState('');
    const [email, setEmail]=useState('');
    const [msg, setMsg]=useState('');

    const handleSubmit=(e)=>{
        e.preventDefault();
        const template='template_GW1QnjxZ';
        const userId='user_PyQ8Ekwhfkjs29drnXOsz';
        const service='default_service'
        const params={name:name, email:email,message:msg};
        console.log(params)
        console.log(template)
        console.log(userId)

        emailjs.send(service, template, params, userId)
            .then((response) =>{
                setName('');
                setEmail('');
                setMsg('');
                console.log(`success! here is your ${response}`);
            }, (error)=>{console.log(error)});

    }
    useEffect(()=>
    <Typical/>)
    return(
    <>
    <div className='photo'>
        <img className='city' src={Background} alt="Photo by: Gautum Krishnan " />
    </div>

    <div className='header'>
        <h2 className="greeting">Hi, I'm Summer Bakr.</h2>
        <h3 className="subgreeting">I'm a </h3>
        <div className="typing">
        <Typical
            steps={['budding software developer', 2000, 'avid learner', 2000, "dedicated and passionate individual", 1000]}
            loop={Infinity}
            wrapper="p"
        /></div>
        <h3 className='about'>Chicago-based Software Engineer with a background in Full Stack Development. I have an affinity for learning new technologies, and particulary enjoy working with server-side technologies. My experiences allow me to bring a unique and fresh perspective to any team. </h3>
    </div>
    <div className='contact'>
        <h2>Want to get in touch?</h2>
        <p>I am currently looking for opportunities in software development.</p>
    <form id="contact-form"  onSubmit={handleSubmit}>
    <div className="form-group">
        <label htmlFor="name">Name</label>
        <input 
        type="text" 
        value={name}
        onChange={(e)=>setName(e.target.value)}
        className="form-control" />
    </div>
    <div className="form-group">
        <label htmlFor="exampleInputEmail1">Email address</label>
        <input 
        type="email" 
        value={email}
        onChange={(e)=>setEmail(e.target.value)}
        className="form-control" aria-describedby="emailHelp" />
    </div>
    <div className="form-group">
        <label htmlFor="message">Message</label>
        <textarea onChange={(e)=>setMsg(e.target.value)}  className="form-control" rows="5"></textarea>
    </div>
    <button type="submit" className="btn btn-primary">Submit</button>
</form>
    </div>
    </>
    );
}
export default LandingPage;
